import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [code, setCode] = useState('');
    const [fromLanguage, setFromLanguage] = useState('Python');
    const [toLanguage, setToLanguage] = useState('JavaScript');
    const [convertedCode, setConvertedCode] = useState('');

    const handleConvert = async () => {
        try {
            const response = await axios.post('http://localhost:5000/convert', {
                code,
                fromLanguage,
                toLanguage,
            });
            setConvertedCode(response.data.convertedCode);
        } catch (error) {
            console.error(error);
            alert('Failed to convert code');
        }
    };

    return (
        <div className="App">
            <h1>AI Code Converter</h1>
            <textarea
                value={code}
                onChange={(e) => setCode(e.target.value)}
                placeholder="Enter your code here"
                rows={10}
                cols={50}
            />
            <div>
                <label>From:</label>
                <select value={fromLanguage} onChange={(e) => setFromLanguage(e.target.value)}>
                    <option value="Python">Python</option>
                    <option value="JavaScript">JavaScript</option>
                    <option value="Java">Java</option>
                </select>
                <label>To:</label>
                <select value={toLanguage} onChange={(e) => setToLanguage(e.target.value)}>
                    <option value="JavaScript">JavaScript</option>
                    <option value="Python">Python</option>
                    <option value="Java">Java</option>
                </select>
            </div>
            <button onClick={handleConvert}>Convert</button>
            <h2>Converted Code:</h2>
            <pre>{convertedCode}</pre>
        </div>
    );
}

export default App;
